
/**
 * @brief Provides support for dialing, answering, and managing phone calls on a device with telephony support.
 * @link https://developer.mozilla.org/en-US/docs/Web/API/Telephony
 */
function Telephony() {

	/**
	 * @brief Returns a TelephonyCall object indicating the call that is currently active. The active call is the one that is currently receiving input from the microphone as well as tones generated by calls to the Telephony.startTone() method.
	 * @link https://developer.mozilla.org/en-US/docs/Web/API/Telephony/active
	 */
	this.active = '';

	/**
	 * @brief Returns a list of TelephonyCall objects representing the currently connected telephone calls.
	 * @link https://developer.mozilla.org/en-US/docs/Web/API/Telephony/calls
	 */
	this.calls = '';

	/**
	 * @brief Indicates whether or not the microphone is muted for the purposes of telephony.
	 * @link https://developer.mozilla.org/en-US/docs/Web/API/Telephony/muted
	 */
	this.muted = '';

	/**
	 * @brief Indicates whether or not the speakerphone functionality of the phone is enabled.
	 * @link https://developer.mozilla.org/en-US/docs/Web/API/Telephony/speakerEnabled
	 */
	this.speakerEnabled = '';

	/**
	 * @brief This method dials the phone and returns a new TelephonyCall object representing the new call.
	 * @link https://developer.mozilla.org/en-US/docs/Web/API/Telephony/dial
	 * @param number - A string indicating the phone number to dial.
	 */
	this.dial = function(number) {};

	/**
	 * @brief The sendTones() method of the Telephony Interface generates multiple DTMF tones, with a specified duration for each tone, and an optional delay before generation starts. The tones are sent to the currently active call.
	 * @link https://developer.mozilla.org/en-US/docs/Web/API/Telephony/sendTones
	 * @param dtmfChars - A DOMString containing the DTMF characters to generate; these must be characters from 0-9, "*", or "+".
	 * @param pauseDuration - (optional) The time to wait before sending the tones. The default value if none is specified is 3000ms.
	 * @param toneDuration - (optional) The length each tone should be. The default value if none is specified is 70ms.
	 * @param serviceId - (optional) The service ID, as specified in the dom.telephony.defaultServiceId setting.
	 */
	this.sendTones = function(dtmfChars, pauseDuration, toneDuration, serviceId) {};

	/**
	 * @brief This method begins generating the tone for the specified DTMF character. The tone is sent to the currently active call, and continues to sound until a corresponding call to Telephony.stopTone().
	 * @link https://developer.mozilla.org/en-US/docs/Web/API/Telephony/startTone
	 * @param dtmfChar - A string indicating the DTMF character to dial; this must be a character from 0-9, "*", or "+".
	 */
	this.startTone = function(dtmfChar) {};

	/**
	 * @brief Stops transmitting the currently-playing DTMF tone started by a prior call to Telephony.startTone().
	 * @link https://developer.mozilla.org/en-US/docs/Web/API/Telephony/stopTone
	 */
	this.stopTone = function() {};

}

